mapper.select:
SELECT DISTINCT cg.smallcategories, IF(temp.count is null, 0 ,temp.count) as count from cgshangbao cg LEFT JOIN ( SELECT smallcategories, count(1) AS count FROM cgshangbao
WHERE DATE_FORMAT(createTime, '%Y-%m-%d') = #{calDay}
GROUP BY smallcategories ) temp on cg.smallcategories = temp.smallcategories order by count

SELECT DISTINCT a.source as 'name', IF(temp.amount is NULL,0, temp.amount) as 'value' from cgshangbao a LEFT JOIN ( select count(1) amount, source from cgshangbao
where DATE_FORMAT(createTime,'%Y-%m-%d') = #{calDay}
group by source order by amount) temp on temp.source = a.source

SELECT DISTINCT a.people as 'name', IF(temp.amount is NULL,0, temp.amount) as 'value' from cgshangbao a LEFT JOIN ( select count(1) amount, people from cgshangbao
where DATE_FORMAT(createTime,'%Y-%m-%d') = #{calDay}
group by people) temp on temp.people = a.people WHERE source != '综治网格联动问题' order by 'value'
