mapper.select:
select count(1) from cguser where delflag = 'A'
and yhzh like concat('%', #{page.yhzh}, '%')
and createTime > #{page.startTime}
and createTime < #{page.endTime}
and (name like concat('%', #{page.key}, '%') or phone like concat('%', #{page.key}, '%') or department like concat('%', #{page.key}, '%') or position like concat('%', #{page.key}, '%') or number like concat('%', #{page.key}, '%') or groupId like concat('%', #{page.key}, '%'))
and createId like concat('%', #{page.createId}, '%')

select
from cguser where delflag = 'A'
and yhzh like concat('%', #{page.yhzh}, '%')
and createTime > #{page.startTime}
and createTime < #{page.endTime}
and (name like concat('%', #{page.key}, '%') or phone like concat('%', #{page.key}, '%') or department like concat('%', #{page.key}, '%') or position like concat('%', #{page.key}, '%') or number like concat('%', #{page.key}, '%') or groupId like concat('%', #{page.key}, '%'))
and createId like concat('%', #{page.createId}, '%')
order by createTime desc limit #{page.start},#{page.pageSize}
