mapper.select:
select count(1) from hidden_danger where isdeleted = '0'
and (address like concat('%', #{page.key},'%') or name like concat('%', #{page.key},'%') or number like concat('%', #{page.key},'%') or people like concat('%', #{page.key},'%') )
and createTime > #{page.startTime}
and createTime < #{page.endTime}
and state like concat('%', #{page.state}, '%')

select
from hidden_danger where isdeleted = '0'
and (address like concat('%', #{page.key},'%') or name like concat('%', #{page.key},'%') or number like concat('%', #{page.key},'%') or people like concat('%', #{page.key},'%') )
and createTime > #{page.startTime}
and createTime < #{page.endTime}
and state like concat('%', #{page.state}, '%')
order by createTime desc limit #{page.start},#{page.pageSize}

mapper.update:

update hidden_danger
set state = #{state,jdbcType=VARCHAR},
department = #{department,jdbcType=VARCHAR},
completeDate = #{completeDate,jdbcType=DATE}
where id = #{id,jdbcType=INTEGER}


update hidden_danger
set state = #{state,jdbcType=VARCHAR},
assign = #{assign,jdbcType=VARCHAR},
completeDate = #{completeDate,jdbcType=DATE}
where id = #{id,jdbcType=INTEGER}


update hidden_danger
set state = #{state,jdbcType=VARCHAR},
solve = #{solve,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}
