and (Categories like concat('%', #{page.key}, '%') or phone like concat('%', #{page.key}, '%') or people like concat('%', #{page.key}, '%') or smallcategories like concat('%', #{page.key}, '%') or miaoshu like concat('%', #{page.key}, '%'))
