mapper.select:
SELECT t1.*,t2.VechiclesTypeName,t3.NetGridName FROM TEKeyVehicles t1 left join TECODEVechiclesType t2 on t1.VechiclesTypeValue=t2.VechiclesTypeValue left join TENetGrid t3 on t1.NetGridID=t3.NetGridID where 1=1
and t1.VehiclesHolderName like CONCAT(CONCAT('%', #{name}), '%')
and t1.VehiclesHolderCertID= #{certID}
and t1.VechiclesNumber= #{number}
and t1.NetGridID= #{netGridID}
limit #{start}, #{pageSize};

SELECT count(*) FROM TEKeyVehicles t1 left join TECODEVechiclesType t2 on t1.VechiclesTypeValue=t2.VechiclesTypeValue left join TENetGrid t3 on t1.NetGridID=t3.NetGridID where 1=1
and t1.VehiclesHolderName like CONCAT(CONCAT('%', #{name}), '%')
and t1.VehiclesHolderCertID= #{certID}
and t1.VechiclesNumber= #{number}
and t1.NetGridID= #{netGridID}
