select
from keypro_problem where delflag != 'D' and process_state != '4'
and (keypro_problem_title like concat('%',#{page.key},'%') or keypro_problem_content like concat('%',#{page.key},'%') or keypro_problem_type like concat('%',#{page.key},'%') or keypro_problem_address like concat('%',#{page.key},'%') or keypro_problem_report_person like concat('%',#{page.key},'%') or lianren like concat('%',#{page.key},'%') or paifaren like concat('%',#{page.key},'%') or paifadep like concat('%',#{page.key},'%') or paifadep like concat('%',#{page.key},'%') )
and keypro_problem_report_person = #{name}
and process_state like concat('%', #{page.state}, '%')
and keypro_problem_report_time > #{page.startTime}
and keypro_problem_report_time < #{page.endTime}
and create_id like concat('%', #{page.createId}, '%')
order by create_time desc limit #{page.start},#{page.pageSize}

