mapper.update:
update TENetGridEvent
       set
       EventStatusTypeValue = #{type}
       where EventID = #{id}

mapper.select:
SELECT t1.*,EventTypeName,EventStatusTypeName,NetGridName FROM TENetGridEvent t1 left join TECODENetGridEventType t2 on t1.EventTypeValue=t2.EventTypeValue left join TECODENetGridEventStatusType t3 on t1.EventStatusTypeValue=t3.EventStatusTypeValue left join TENetGrid t4 on t1.NetGridID=t4.NetGridID left join TEUser t5 on CurrentResponsiblePersonCertID=UserID WHERE 1=1 and UserID=#{id}
and t1.EventKind=#{kind}
and t1.EventName like CONCAT(CONCAT('%', #{name}), '%')
and t1.NetGridID=#{netGridID}
and t1.EventStatusTypeValue=#{status}
order by EventStartDateTime desc limit #{start}, #{pageSize}

SELECT count(*) FROM TENetGridEvent t1 left join TECODENetGridEventType t2 on t1.EventTypeValue=t2.EventTypeValue left join TECODENetGridEventStatusType t3 on t1.EventStatusTypeValue=t3.EventStatusTypeValue left join TENetGrid t4 on t1.NetGridID=t4.NetGridID left join TEUser t5 on CurrentResponsiblePersonCertID=UserID WHERE 1=1 and UserID=#{id}
and t1.EventKind=#{kind}
and t1.EventName like CONCAT(CONCAT('%', #{name}), '%')
and t1.NetGridID=#{netGridID}
and t1.EventStatusTypeValue=#{status}

SELECT t1.*,EventTypeName,EventStatusTypeName,NetGridName FROM TENetGridEvent t1 left join TECODENetGridEventType t2 on t1.EventTypeValue=t2.EventTypeValue left join TECODENetGridEventStatusType t3 on t1.EventStatusTypeValue=t3.EventStatusTypeValue left join TENetGrid t4 on t1.NetGridID=t4.NetGridID left join TRNetGridEventDistribute t5 on t1.EventID=t5.EventID where 1=1 and (t1.EventStatusTypeValue='01' or t1.EventStatusTypeValue='03' or t1.EventStatusTypeValue='04' or t5.UserID=#{id})
and t1.EventKind=#{kind}
and t1.EventName like CONCAT(CONCAT('%', #{name}), '%')
and t1.NetGridID=#{netGridID}
and t1.EventStatusTypeValue=#{status}
group by t1.EventID order by EventStartDateTime desc limit #{start}, #{pageSize}

