SELECT count(*) FROM TENetGridEvent t1 left join TECODENetGridEventType t2 on t1.EventTypeValue=t2.EventTypeValue left join TECODENetGridEventStatusType t3 on t1.EventStatusTypeValue=t3.EventStatusTypeValue left join TENetGrid t4 on t1.NetGridID=t4.NetGridID where 1=1
