select tg.NetGridName name,ifnull(tt.solved,0) solved,ifnull(tt.unsolved,0) unsolved from TENetGrid tg left join
(select NetGridName, sum(case when EventStatusTypeValue='07' then 1 else 0 end) solved,
sum(case when EventStatusTypeValue!='07' then 1 else 0 end)unsolved
from TENetGridEvent t1
left join TENetGrid t2 on t1.NetGridID=t2.NetGridID
where 1=1

    and EventDate >=#{start}


    and EventDate <=#{end}

group by NetGridName) tt on tg.NetGridName=tt.NetGridName where tg.FatherNetGridID=-1 order by tg.NetGridID;

select ifnull(sum(case when EventStatusTypeValue='07' then 1 else 0 end),0) solved,
ifnull(sum(case when EventStatusTypeValue!='07' then 1 else 0 end),0)unsolved
from TENetGridEvent
where 1=1

    and EventDate >=#{start}


    and EventDate <=#{end}
