mapper.select:
select count(1) from security_inf where 1=1
and (title like concat('%', #{page.key},'%') or content like concat('%', #{page.key},'%') or yhzh like concat('%', #{page.key},'%') or createId like concat('%', #{page.key},'%') or enclosure like concat('%', #{page.key},'%') )
and createTime > #{page.startTime}
and createTime < #{page.endTime}

select
from security_inf where 1=1
and (title like concat('%', #{page.key},'%') or content like concat('%', #{page.key},'%') or yhzh like concat('%', #{page.key},'%') or createId like concat('%', #{page.key},'%') or enclosure like concat('%', #{page.key},'%') )
and createTime > #{page.startTime}
and createTime < #{page.endTime}
limit #{page.start},#{page.pageSize}

select count(1) from security_inf where isdeleted = '0'
and (title like concat('%', #{page.key},'%') or content like concat('%', #{page.key},'%') or yhzh like concat('%', #{page.key},'%') or createId like concat('%', #{page.key},'%') or enclosure like concat('%', #{page.key},'%') )
and createTime > #{page.startTime}
and createTime < #{page.endTime}

select
from security_inf where isdeleted = '0'
and (title like concat('%', #{page.key},'%') or content like concat('%', #{page.key},'%') or yhzh like concat('%', #{page.key},'%') or createId like concat('%', #{page.key},'%') or enclosure like concat('%', #{page.key},'%') )
and createTime > #{page.startTime}
and createTime < #{page.endTime}
order by createTime desc limit #{page.start},#{page.pageSize}

select count(1) from security_inf where isdeleted = '0'
and id in
#{newsId}
and (title like concat('%', #{page.key},'%') or content like concat('%', #{page.key},'%') or yhzh like concat('%', #{page.key},'%') or createId like concat('%', #{page.key},'%') or enclosure like concat('%', #{page.key},'%') )
and createTime > #{page.startTime}
and createTime < #{page.endTime}

select a.id,a.title,a.content,b.is_receive as yhzh,a.createId,a.createTime, a.isdeleted,a.enclosure from security_inf a left join security_inf_receive b on a.id = b.news_id where a.isdeleted = '0' and b.user_id = #{page.userId}
and a.id in
#{newsId}
and (a.title like concat('%', #{page.key},'%') or a.content like concat('%', #{page.key},'%') or b.is_receive like concat('%', #{page.key},'%') or a.createId like concat('%', #{page.key},'%') or a.enclosure like concat('%', #{page.key},'%') )
and a.createTime > #{page.startTime}
and a.createTime < #{page.endTime}
order by a.createTime desc limit #{page.start},#{page.pageSize}
