mapper.select:
select count(1) from security_train where isdeleted = '0'
and (title like concat('%', #{page.key},'%') or enclosure like concat('%', #{page.key},'%') or yhzh like concat('%', #{page.key},'%') or createId like concat('%', #{page.key},'%') or content like concat('%', #{page.key},'%') )
and createTime > #{page.startTime}
and createTime < #{page.endTime}

select
from security_train where isdeleted = '0'
and (title like concat('%', #{page.key},'%') or enclosure like concat('%', #{page.key},'%') or yhzh like concat('%', #{page.key},'%') or createId like concat('%', #{page.key},'%') or content like concat('%', #{page.key},'%') )
and createTime > #{page.startTime}
and createTime < #{page.endTime}
order by createTime desc limit #{page.start},#{page.pageSize}

mapper.update:

update security_train
set title = #{title,jdbcType=VARCHAR},
  enclosure = #{enclosure,jdbcType=VARCHAR},
  yhzh = #{yhzh,jdbcType=VARCHAR},
  content = #{content,jdbcType=VARCHAR}
  where id = #{id,jdbcType=INTEGER}
