mapper.select:
SELECT t1.*,OrgTypeName,LevelTypeName,NetGridName
FROM TEOrgBasicInf t1
left join TECODEOrganizationType t2 on t1.OrgTypeValue=t2.OrgTypeValue
left join TECODELevelType t3 on t1.LevelTypeValue=t3.LevelTypeValue
left join TENetGrid t5 on t1.NetGridID=t5.NetGridID
where 1=1

    and t1.OrgName like CONCAT(CONCAT('%',
    #{orgName}), '%')


    and t1.OrgTypeValue=#{orgType}


    and t1.NetGridID=#{netGridID}


    and t1.LevelTypeValue=#{levelType}

limit #{start}, #{pageSize}

SELECT count(*) FROM TEOrgBasicInf t1 left join TECODEOrganizationType t2 on t1.OrgTypeValue=t2.OrgTypeValue left join TECODELevelType t3 on t1.LevelTypeValue=t3.LevelTypeValue left join TENetGrid t5 on t1.NetGridID=t5.NetGridID where 1=1
and t1.OrgName like CONCAT(CONCAT('%', #{orgName}), '%')
and t1.OrgTypeValue=#{orgType}
and t1.NetGridID=#{netGridID}
and t1.LevelTypeValue=#{levelType}
