mapper.select:
select OrgMemberID, OrgName, Name, Sex, EthnicityName as Ethnicity, PoliticsTypeName as Politics, CertID, date_format(BirthDate, '%Y-%m%-%d') as BirthDate, positionTypeName, LevelTypeName, Education, MobileTel, FixedTel, Status from TEOrgMember left join TECODEEthnicityType on TEOrgMember.Ethnicity=TECODEEthnicityType.Ethnicity left join TECODEPoliticsType on TEOrgMember.Politics=TECODEPoliticsType.PoliticsTypeValue left join TECODEPersonPositionType on TEOrgMember.PositionTypeValue = TECODEPersonPositionType.PositionTypeValue left join TECODEPersonLevelType on TEOrgMember.LevelTypeValue = TECODEPersonLevelType.LevelTypeValue left join TEOrgBasicInf on TEOrgMember.OrgID = TEOrgBasicInf.OrgID where 1=1
and name LIKE CONCAT(CONCAT('%', #{name}), '%')
and TEOrgMember.PositionTypeValue = #{positionTypeValue}
and status = #{status}
limit #{page} ,
#{pageSize}
10
limit #{pageSize} 10

select count(OrgMemberID)
from TEOrgMember
where 1=1

	and name LIKE CONCAT(CONCAT('%', #{name}), '%')


	and TEOrgMember.positionTypeValue = #{positionTypeValue}


	and status = #{status}

select OrgMemberID, OrgName, Name, Sex, EthnicityName as Ethnicity, PoliticsTypeName as Politics, CertID, date_format(BirthDate, '%Y-%m%-%d') as BirthDate, positionTypeName, LevelTypeName, Education, MobileTel, FixedTel, Status from TEOrgMember left join TECODEEthnicityType on TEOrgMember.Ethnicity=TECODEEthnicityType.Ethnicity left join TECODEPoliticsType on TEOrgMember.Politics=TECODEPoliticsType.PoliticsTypeValue left join TECODEPersonPositionType on TEOrgMember.PositionTypeValue = TECODEPersonPositionType.PositionTypeValue left join TECODEPersonLevelType on TEOrgMember.LevelTypeValue = TECODEPersonLevelType.LevelTypeValue left join TEOrgBasicInf on TEOrgMember.OrgID = TEOrgBasicInf.OrgID where 1=1
and name LIKE CONCAT(CONCAT('%', #{name}), '%')
and CertID = #{id}
