mapper.select:

select t2.* from TRDisputeEventPicture t1 left join TEPicture t2
on
t1.PictureID=t2.PictureID where t1.DisputeEventID=#{disputeEventID};


select t2.* from TRServedRecordPicture t1 left join TEPicture t2
on
t1.PictureID=t2.PictureID where t1.ServedRecordID=#{orgMServedPID};


select t2.* from TREnterprisePicture t1 left join TEPicture t2
on
t1.PictureID=t2.PictureID where t1.EnterpriseUSCode=#{code};


select t2.* from TRTrafficAccidentPicture t1 left join TEPicture t2
on
t1.PictureID=t2.PictureID where t1.TrafficAccidentID=#{id};


select t2.* from TRSecurityEventPicture t1 left join TEPicture t2
on
t1.PictureID=t2.PictureID where t1.SSEventID=#{id};


select t2.* from TRScenicAreaDutyPointPicture t1 left join TEPicture t2
on
t1.PictureID=t2.PictureID where t1.ScenicAreaDutyPointID=#{id};


select t2.* from TRSkynetPatrolEventPicture t1 left join TEPicture t2
on
t1.PictureID=t2.PictureID where t1.SPEventID=#{id};

select t2.* from TRNetGridEventPicture t1 left join TEPicture t2
on
t1.PictureID=t2.PictureID where t1.EventID=#{id};

select t2.* from TRSkynetPEDealingPicture t1 left join TEPicture t2 on
t1.PictureID=t2.PictureID left join TRSkynetPatrolEventDealing t3 on
t1.SkynetPatrolEventDealingID=t3.SkynetPatrolEventDealingID where SPEventID=#{id};

select t2.* from TRSSEventDealingPicture t1 left join TEPicture t2 on
t1.PictureID=t2.PictureID left join TRSSEventDealing t3 on
t1.EventDealingID=t3.EventDealingID where EventID=#{id};

select t2.* from TRNetGridEventDealingPicture t1 left join TEPicture t2 on
 t1.PictureID=t2.PictureID left join TRNetGridEventDealing t3 on
 t1.EventDealingID=t3.EventDealingID where EventID=#{id};

 select t2.* from TRTrafficAccidentDealingPicture t1 left join TEPicture t2 on
t1.PictureID=t2.PictureID left join TRTrafficAccidentDealing t3 on
t1.EventDealingID=t3.EventDealingID where EventID=#{id};

select t2.* from TRDisputeDealingPicture t1 left join TEPicture t2 on
t1.PictureID=t2.PictureID left join TRDisputeEventDealing t3 on
t1.EventDealingID=t3.EventDealingID where EventID=#{id};

SELECT t2.* FROM TRKeyVehiclesPicture t1
left join TEPicture t2 on t1.PictureID=t2.PictureID
where t1.KeyVehiclesID=#{id};

SELECT t2.* FROM TRKeyDriverPicture t1
left join TEPicture t2 on t1.PictureID=t2.PictureID
where t1.KeyDriverID=#{id};

select t2.*,t1.SkynetPatrolEventDealingID EventDealingID from TRSkynetPEDealingPicture t1 left join TEPicture t2 on
t1.PictureID=t2.PictureID left join TRSkynetPatrolEventDealing t3 on
t1.SkynetPatrolEventDealingID=t3.SkynetPatrolEventDealingID where SPEventID=#{id};

select t2.*,t1.EventDealingID from TRSSEventDealingPicture t1 left join TEPicture t2 on
t1.PictureID=t2.PictureID left join TRSSEventDealing t3 on
t1.EventDealingID=t3.EventDealingID where EventID=#{id};

select t2.*,t1.EventDealingID from TRTrafficAccidentDealingPicture t1 left join TEPicture t2 on
t1.PictureID=t2.PictureID left join TRTrafficAccidentDealing t3 on
t1.EventDealingID=t3.EventDealingID where EventID=#{id};

select t2.*,t1.EventDealingID from TRDisputeDealingPicture t1 left join TEPicture t2 on
t1.PictureID=t2.PictureID left join TRDisputeEventDealing t3 on
t1.EventDealingID=t3.EventDealingID where EventID=#{id};
