		and (xm like CONCAT('%', #{keyparam.keyword},'%') or xb like CONCAT('%', #{keyparam.keyword},'%')or jzxq like CONCAT('%', #{keyparam.keyword},'%'))


          and create_time >= #{keyparam.startTime}


