mapper.select:
select count(1) from road_protection_road where delflag != '1'
and (road_name like concat('%', #{page.key}, '%') or road_address like concat('%', #{page.key}, '%') or road_charge_person like concat('%', #{page.key}, '%') or road_charge_person_phone like concat('%', #{page.key}, '%') or road_safe_charge_person like concat('%', #{page.key}, '%') or road_safe_charge_person_phone like concat('%', #{page.key}, '%') )
and create_time > #{page.startTime}
and create_time < #{page.endTime}

select
from road_protection_road where delflag != '1'
and (road_name like concat('%', #{page.key}, '%') or road_address like concat('%', #{page.key}, '%') or road_charge_person like concat('%', #{page.key}, '%') or road_charge_person_phone like concat('%', #{page.key}, '%') or road_safe_charge_person like concat('%', #{page.key}, '%') or road_safe_charge_person_phone like concat('%', #{page.key}, '%') )
and create_time > #{page.startTime}
and create_time < #{page.endTime}
limit #{page.start},#{page.pageSize}
