mapper.insert:

insert into TESPProcession (id, ProcessionName, ProcessionContact,
ProcessionDuty, ProcessionLeaderName, ProcessionLeaderContact,
ProcessionNumbers, InspectionArea, Remark
)
values (#{id,jdbcType=BIGINT}, #{processionName,jdbcType=VARCHAR}, #{processionContact,jdbcType=VARCHAR},
#{processionDuty,jdbcType=VARCHAR}, #{processionLeaderName,jdbcType=VARCHAR},
#{processionLeaderContact,jdbcType=VARCHAR},
#{processionNumbers,jdbcType=INTEGER}, #{inspectionArea,jdbcType=BIGINT}, #{remark,jdbcType=VARCHAR}
)

mapper.select:
select t1.id, ProcessionName, ProcessionContact, ProcessionDuty, ProcessionLeaderName, ProcessionLeaderContact, ProcessionNumbers, RegionName, Remark from TESPProcession t1 left join TESPRegion t2 on InspectionArea=t2.id where 1=1
and ProcessionName like CONCAT(CONCAT('%', #{processionName}), '%')
and ProcessionLeaderName like CONCAT(CONCAT('%', #{processionLeaderName}), '%')
and InspectionArea =#{area}
limit #{start}, #{pageSize}
