mapper.update:

update TESPProcessionPhoto
set PersonID = #{personID,jdbcType=BIGINT},
PhotoFilePath = #{photoFilePath,jdbcType=VARCHAR},
PhotoFileName = #{photoFileName,jdbcType=VARCHAR},
PhotoFileSuffix = #{photoFileSuffix,jdbcType=VARCHAR}
where PersonPhotoID = #{personPhotoID,jdbcType=BIGINT}

mapper.select:

select PersonPhotoID, PersonID, PhotoFilePath, PhotoFileName, PhotoFileSuffix
from TESPProcessionPhoto
where PersonPhotoID = #{personPhotoID,jdbcType=BIGINT}

select PersonPhotoID, PersonID, PhotoFilePath, PhotoFileName, PhotoFileSuffix
from TESPProcessionPhoto
where PersonID = #{id,jdbcType=BIGINT}
