mapper.select:
select id, SchoolName,SchoolOrganizationCode, SchoolRegionalismCode, SchoolAddress, SchoolRunningTypeName,SchoolNature, SchoolSuperior, StuNumbers,PrincipalCertID, PrincipalName, PrincipalContact, BranchSecurityName, BranchSecurityContact, SecurityName, SecurityContact, PoliceName, PoliceContact, SecurityNumbers, SchoolLongitude, SchoolLatitude, NetGridName from TESchool t1 left join TECODESchoolRunningType t2 on t1.SchoolRunningType=t2.SchoolRunningTypeValue left join TENetGrid t3 on t1.NetGridID=t3.NetGridID where 1=1
and t1.SchoolName like CONCAT(CONCAT('%', #{name}), '%')
and t1.SchoolRunningType=#{type}
and t1.NetGridID=#{netGridID}
limit #{start}, #{pageSize}

select count(*) from TESchool t1 left join TECODESchoolRunningType t2 on t1.SchoolRunningType=t2.SchoolRunningTypeValue left join TENetGrid t3 on t1.NetGridID=t3.NetGridID where 1=1
and t1.SchoolName like CONCAT(CONCAT('%', #{name}), '%')
and t1.SchoolRunningType=#{type}
and t1.NetGridID=#{netGridID}
