mapper.select:
SELECT t1.ExtendCorrectionerID, t2.Name,t2.Sex,t3.PoliticsTypeName,t2.CertifID,t2.BirthDate,t2.Career,t2.MobileTel FROM TESpecialPopulationExtendAIDSdisease t1 left join TEServedPersonBasic t2 on t1.CertifID=t2.CertifID left join TECODEPoliticsType t3 on t2.Politics=t3.PoliticsTypeValue where 1=1
and t2.Name like CONCAT(CONCAT('%', #{name}), '%')
and t2.CertifID = #{certifID}
and t2.NetGridID= #{netGridID}
limit #{start}, #{pageSize};

SELECT count(*) FROM TESpecialPopulationExtendAIDSdisease t1 left join TEServedPersonBasic t2 on t1.CertifID=t2.CertifID left join TECODEPoliticsType t3 on t2.Politics=t3.PoliticsTypeValue where 1=1
and t2.Name like CONCAT(CONCAT('%', #{name}), '%')
and t2.CertifID = #{certifID}
and t2.NetGridID= #{netGridID}
