mapper.select:
select count(1) from wisdom_park_talent where is_deleted != '1'
and (talent_name like concat('%', #{page.key}, '%') or talent_identity like concat('%', #{page.key}, '%') or talent_education_degree like concat('%', #{page.key}, '%') or graduate_school like concat('%', #{page.key}, '%') or graduate_major like concat('%', #{page.key}, '%') or talent_address like concat('%', #{page.key}, '%') or registered_residence like concat('%', #{page.key}, '%') or enterprise_belong like concat('%', #{page.key}, '%') or is_full_time like concat('%', #{page.key}, '%') or talent_plan_level like concat('%', #{page.key}, '%') or talent_plan_name like concat('%', #{page.key}, '%') or talent_plan_period like concat('%', #{page.key}, '%') or position_x like concat('%', #{page.key}, '%') or position_y like concat('%', #{page.key}, '%') or enterprise_address like concat('%', #{page.key}, '%') )
and talent_plan_start_time >= #{page.startTime}
and talent_plan_start_time <= #{page.endTime}

select
from wisdom_park_talent where is_deleted != '1'
and (talent_name like concat('%', #{page.key}, '%') or talent_identity like concat('%', #{page.key}, '%') or talent_education_degree like concat('%', #{page.key}, '%') or graduate_school like concat('%', #{page.key}, '%') or graduate_major like concat('%', #{page.key}, '%') or talent_address like concat('%', #{page.key}, '%') or registered_residence like concat('%', #{page.key}, '%') or enterprise_belong like concat('%', #{page.key}, '%') or is_full_time like concat('%', #{page.key}, '%') or talent_plan_level like concat('%', #{page.key}, '%') or talent_plan_name like concat('%', #{page.key}, '%') or talent_plan_period like concat('%', #{page.key}, '%') or position_x like concat('%', #{page.key}, '%') or position_y like concat('%', #{page.key}, '%') or enterprise_address like concat('%', #{page.key}, '%') )
and talent_plan_start_time >= #{page.startTime}
and talent_plan_start_time <= #{page.endTime}
order by talent_plan_start_time desc limit #{page.start},#{page.pageSize}
