mapper.select:
select count(1)
from workshop_adm
where isdeleted = '0'

	and (name like concat('%', #{page.key},'%')
		or people like concat('%', #{page.key},'%')
		or phone like concat('%', #{page.key},'%')
	)


	and createTime > #{page.startTime}


	and createTime < #{page.endTime}

select

from workshop_adm
where isdeleted = '0'

	and (name like concat('%', #{page.key},'%')
		or people like concat('%', #{page.key},'%')
		or phone like concat('%', #{page.key},'%')
	)


	and createTime > #{page.startTime}


	and createTime < #{page.endTime}

order by createTime desc
limit
#{page.start},#{page.pageSize}
