mapper.select:
select count(1)
from workshop_ent
where isdeleted = '0'

	and (name like concat('%', #{page.key},'%')
		or survey like concat('%', #{page.key},'%')
		or yhzh like concat('%', #{page.key},'%')
		or createId like concat('%', #{page.key},'%')
	)


	and createTime > #{page.startTime}


	and createTime < #{page.endTime}

select

from workshop_ent
where isdeleted = '0'

	and (name like concat('%', #{page.key},'%')
		or survey like concat('%', #{page.key},'%')
		or yhzh like concat('%', #{page.key},'%')
		or createId like concat('%', #{page.key},'%')
	)


	and createTime > #{page.startTime}


	and createTime < #{page.endTime}

order by createTime desc
limit
#{page.start},#{page.pageSize}
