mapper.select:
select count(1) from workshop_equ where isdeleted = '0'
and (name like concat('%', #{page.key},'%') or number like concat('%', #{page.key},'%') or address like concat('%', #{page.key},'%') or yhzh like concat('%', #{page.key},'%') or createId like concat('%', #{page.key},'%') )
and createTime > #{page.startTime}
and createTime < #{page.endTime}

select
from workshop_equ where isdeleted = '0'
and (name like concat('%', #{page.key},'%') or number like concat('%', #{page.key},'%') or address like concat('%', #{page.key},'%') or yhzh like concat('%', #{page.key},'%') or createId like concat('%', #{page.key},'%') )
and createTime > #{page.startTime}
and createTime < #{page.endTime}
order by createTime desc limit #{page.start},#{page.pageSize}
